// Extract and validate input parameters
const question = $input.item.json.body.question || '';
const mode = $input.item.json.body.mode || 'default';
const sessionId = $input.item.json.body.session_id || 'default';
const timestamp = $input.item.json.body.timestamp || new Date().toISOString();

// Mode-specific system message adjustments
const modeConfigurations = {
  'interview': {
    suffix: 'Keep responses concise, professional, and informative. Focus on achievements and measurable outcomes.',
    style: 'professional'
  },
  'storytelling': {
    suffix: 'Provide longer, more reflective narratives with context and personal insights. Share the story behind the facts.',
    style: 'narrative'
  },
  'facts': {
    suffix: 'Respond in bullet points or TL;DR format. Be concise and factual.',
    style: 'concise'
  },
  'humble_brag': {
    suffix: 'Answer with confidence and highlight achievements prominently, while staying grounded in truth.',
    style: 'confident'
  },
  'reflection': {
    suffix: 'Focus on self-awareness, growth areas, work preferences, and personal insights about collaboration and energy.',
    style: 'reflective'
  },
  'default': {
    suffix: 'Maintain Dean\'s authentic, conversational tone with technical depth when appropriate.',
    style: 'authentic'
  }
};

const config = modeConfigurations[mode] || modeConfigurations['default'];

return {
  question,
  mode,
  sessionId,
  timestamp,
  modeConfig: config,
  processedAt: new Date().toISOString()
};

// Format response with metadata and mode information
const aiResponse = $input.item.json.message?.content || $input.item.json.output || 'I apologize, but I encountered an issue processing your question.';
const originalInput = $('Process Input').first().json;

// Extract key topics mentioned in response for analytics
const keyTopics = [];
if (aiResponse.toLowerCase().includes('project')) keyTopics.push('projects');
if (aiResponse.toLowerCase().includes('skill')) keyTopics.push('technical_skills');
if (aiResponse.toLowerCase().includes('experience')) keyTopics.push('work_experience');
if (aiResponse.toLowerCase().includes('automation')) keyTopics.push('automation');
if (aiResponse.toLowerCase().includes('n8n')) keyTopics.push('n8n');

return {
  status: 'success',
  response: aiResponse,
  metadata: {
    mode_used: originalInput.mode,
    style: originalInput.modeConfig.style,
    session_id: originalInput.sessionId,
    response_timestamp: new Date().toISOString(),
    topics_covered: keyTopics,
    response_length: aiResponse.length,
    responder: 'Dean Jeggels Personal AI Assistant'
  },
  follow_up_suggestions: [
    'Tell me more about your technical approach',
    'What challenges did you face in that project?',
    'How do you stay updated with new technologies?',
    'What advice would you give to someone starting in AI automation?'
  ]
};
